		var ListStr		=	'<table align="left" width="200" height="180" cellpadding="0" cellspacing="0" style="border:1px solid #999999;font-size:12px; font-family:Arial; color:#555555;background-color:#FFFFFF;filter:alpha(opacity=95);">'
